import "./App.css";
import React, {useState, useEffect} from "react";

import Inventory from "./Inventory.js";
import Scan from "./Scan.js";

function App() {

  const [screen, setScreen] = useState("scan");
  /*
  let childRef = React.createRef();

  
  if ((childRef.current.results).length == 1) {
    console.log(childRef.current.results[(childRef.current.results).length-1]);
    cur_screen = <Inventory result={childRef.current.results[(childRef.current.results).length-1]} />
  }
  */
  let childState;
  const eventhandler = data => childState = data;
  /* childState.results[0]["codeResult"]["code"] to access the actual code */
  let cur_screen;
  if (childState.results.length == 1) {
    cur_screen = <Inventory result={childState.results[0]["codeResult"]["code"]} />;
  }
  else if (screen === "scan") {
    cur_screen = <Scan onChange={eventhandler} />;
  }
  else {
    cur_screen = <Inventory />;
  }

  const changeScreen = () => {
    if (screen === "scan") {
      cur_screen = <Inventory />;
      setScreen("inventory");
    }
    else {
      cur_screen = <Scan />;
      setScreen("scan");
    }
  }

  return (
    <div>
      <button onClick={changeScreen}>Switch Scan</button>
      {cur_screen}
    </div>
  );
}

export default App;
