import "./App.css";
import React, {useState, useEffect} from "react";
import Axios from "axios";

function App() {
  const [itemName, setItemName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [itemList, setItemList] = useState([]);
  const [newItemName, setNewItemName] = useState("");
  const [newQuantity, setNewQuantity] = useState(0);
  useEffect(() => {
    Axios.get("http://localhost:3001/read").then((response) => { //promise
      setItemList(response.data);
      itemList.forEach(element => {
        element["newItemName"] = "";
        element["newQuantity"] = "";
      });
    });
  }, []);
  const addItem = () => {
    Axios.post("http://localhost:3001/create", {itemName: itemName, quantity: quantity,}).then(() => {
      setItemList(itemList.concat({itemName: itemName, quantity: quantity}));
      itemList[itemList.length-1]["newItemName"] = "";
      itemList[itemList.length-1]["newQuantity"] = "";
      setItemName("");
      setQuantity("");
    });
    console.log("Item added to database");
  };
  const updateItem = (id) => {
    Axios.put("http://localhost:3001/update", {id: id, newItemName: newItemName, newQuantity: newQuantity,}).then(() => {
      setItemList(itemList.map((item) => {
        if (item._id === id) {
          item.itemName = newItemName;
          item.quantity = newQuantity;
          item.newQuantity = "";
          item.newItemName = "";
        }
        return item;
      }));
    });
    console.log("Item updated in database");
  };
  const deleteItem = (id) => {
    Axios.delete(`http://localhost:3001/delete/${id}`).then(() => {
      setItemList(itemList.filter((item) => item._id !== id));
    });
    console.log("Item deleted from database");
  };
  return (
    <div className="App">
      <h1>CRUD APPLICATION</h1>

      <div className="form">
        <h1>Add an Item</h1>
        <label>Item Name:</label>
        <input type="text" name="itemName" value={itemName} onChange={(event) => {
          setItemName(event.target.value);
        }}/>
        <label>Quantity:</label>
        <input type="number" name="quantity" value={quantity} onChange={(event) => {
          setQuantity(event.target.value);
        }}/>
        <button onClick={addItem}>Add Item</button>

      </div>
        <h1>Current Inventory</h1>
        <table>
          <thead>
            <tr>
              <th>Item Name</th>
              <th>Quantity</th>
              <th>Edit</th>
            </tr>
          </thead>
          <tbody>
            {
              itemList.map((item, key) => {
                return (
                  <tr key={key} className="listItem">
                    <td>{item.itemName}</td>
                    <td>{item.quantity}</td>
                    <td>
                      <input type="text" placeholder="Updated Item Name" value={item.newItemName} onChange={(event) => {
                        setNewItemName(event.target.value);
                      }}/>
                      <input type="number" placeholder="Updated Quantity" value={item.newQuantity} onChange={(event) => {
                        setNewQuantity(event.target.value);
                      }}/>
                      <button onClick={() => updateItem(item._id)}>Update</button>
                      <button onClick={() => deleteItem(item._id)}>Delete</button>
                    </td>
                  </tr>
                );
              })
            }
          </tbody>
        </table>
    </div>
  );
}

export default App;
