import "./App.css";
import React, {useState, useEffect} from "react";
import Axios from "axios";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import Inventory from "./Inventory.js";
import Scan from "./Scan.js";



function App() {

  const [screen, setScreen] = useState("scan");

  let cur_screen;
  if (screen === "scan") {
    cur_screen = <Scan />;
  }
  else {
    cur_screen = <Inventory />;
  }

  const changeScreen = () => {
    if (screen === "scan") {
      cur_screen = <Inventory />;
    }
  }

  return (
    <div>
      <button onClick={setScreen("scan")}>Switch Scan</button>
      <button onClick={setScreen("inventory")}>Switch to Inventory</button>
      {cur_screen}
    </div>
  );
}

export default App;
