import "./App.css";
import React, {useState, useEffect} from "react";

import Inventory from "./Inventory.js";
import Scan from "./Scan.js";

function App() {

  const [screen, setScreen] = useState("scan");
  let childRef = React.createRef();

  let cur_screen;
  if (length(childRef.current) == 1) {
    console.log(childRef[length(childRef)-1]);
    cur_screen = <Inventory result={childRef[length(childRef)-1]} />
  }
  else if (screen === "scan") {
    cur_screen = <Scan ref={this.resultsRef} />;
  }
  else {
    cur_screen = <Inventory />;
  }

  const changeScreen = () => {
    if (screen === "scan") {
      cur_screen = <Inventory />;
      setScreen("inventory");
    }
    else {
      cur_screen = <Scan />;
      setScreen("scan");
    }
  }

  return (
    <div>
      <button onClick={changeScreen}>Switch Scan</button>
      {cur_screen}
    </div>
  );
}

export default App;
