import "./App.css";
import React, {useState, useEffect} from "react";
import Axios from "axios";

function App() {
  const [itemName, setItemName] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [itemList, setItemList] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3001/read").then((response) => { //this is a promise
      setItemList(response.data);
    });
  }, []);
  const addItem = () => {
    Axios.post("http://localhost:3001/create", {itemName: itemName, quantity: quantity,});
    console.log("Item added to database");
  };
  return (
    <div className="App">
      <h1>CRUD APPLICATION</h1>

      <div className="form">
        <h1>Add an Item</h1>
        <label>Item Name:</label>
        <input type="text" name="itemName" onChange={(event) => {
          setItemName(event.target.value);
        }}/>
        <label>Quantity:</label>
        <input type="number" name="quantity" onChange={(event) => {
          setQuantity(event.target.value);
        }}/>
        <button onClick={addItem}>Submit</button>

        <h1>Current Inventory</h1>
        {
          itemList.map((item) => {
            return (
              <div>
                <p>{item.itemName}</p>
                <p>{item.quantity}</p>
              </div>
            );
          })
        }
      </div>
    </div>
  );
}

export default App;
